-- ============================================
-- STEM Occupations Import (100 occupations)
-- Run this first in Supabase SQL Editor
-- ============================================

-- Step 1: Upsert into onet_stem_membership

-- Step 2: Update onet_occupation_enrichment
UPDATE onet_occupation_enrichment
SET is_stem = true
WHERE occupation_code IN (
  SELECT occupation_code 
  FROM onet_stem_membership 
  WHERE is_official_stem = true
);

-- Step 3: Verify
SELECT 
  'STEM Import Complete' as status,
  COUNT(*) as stem_count 
FROM onet_occupation_enrichment 
WHERE is_stem = true;
